  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'doc5',
	title: 'Dataset Collection',
	sidebar_label: 'Dataset',
	custom_edit_url: null
};
export const metadata = {
  "unversionedId": "doc5",
  "id": "doc5",
  "isDocsHomePage": false,
  "title": "Dataset Collection",
  "description": "In order to obtain our data to accomplish the two goals, we followed a series of steps to collect data from 7 participants. Our data collection pipeline is as follows :",
